import { Literal, Parent } from 'unist';

/**
 * Copyright 2023 Design Barn Inc.
 */

declare type RootType = 'root';
declare type RuleType = 'rule';
declare type DeclarationType = 'declaration';
interface Declaration extends Literal {
    important: boolean;
    property: string;
    type: DeclarationType;
    value: string;
}
interface Rule extends Parent {
    children: Declaration[];
    selector: string;
    selectors: string[];
    type: RuleType;
}
interface Root extends Parent {
    children: Rule[];
    type: RootType;
}
declare type Node = Root | Rule | Declaration;
declare type NodeType = Node['type'];

export { Declaration, DeclarationType, Node, NodeType, Root, RootType, Rule, RuleType };
